import React, {useEffect, useState, }  from "react";
import "../Estilos/producto.css";

import 'react-toastify/dist/ReactToastify.css';


class listarProyectos extends React.Component{
  insertar= ()=>{
    var valorNuevo= {...this.state.form};
    valorNuevo.id=this.state.data.length+1;
    var lista= this.state.data;
    lista.push(valorNuevo);
    this.setState({ modalInsertar: false, data: lista });
  }
  render() {
  return(
    
  <div className="con">
  <div className="login_Developer2">
              <h3>Listar proyectos</h3>
          <table className="table">
          <thead>
            <tr>
              <th> Nombre </th>
              <th>Objetivos generales  </th>
              <th>Objetivos especificos  </th>
              <th>Presupuesto </th>
              <th>Fecha inicial</th>
              <th>Fecha final </th>
              <th>Documento</th>
              <th>Nombre del lider</th>
              <th>Estado del proyecto</th>
              <th>Fase del proyecto</th>
            </tr>
          </thead>
          

          <tbody>
                        {this.state.data.map((dato) => (
                            <tr key={dato.id}>
                                <td>{dato.id}</td>
                                <td>{dato.nombrePro}</td>
                                <td>{dato.inscripcion}</td>
                                <td>
                                    <button
                                        color="primary"
                                        onClick={() => this.mostrarModalActualizar(dato)}
                                    >
                                        Editar
                                    </button>{" "}
                                    <button color="danger" onClick={() => this.eliminar(dato)}>Eliminar</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                    </table>
          </div>
  
  </div>
  )
   }
  }
  
  export default listarProyectos;
