                        <label>
                            Nombre del proyecto:
                        </label>
                        <input
                            className="form-control"
                            name="nombrePro"
                            type="text"
                            onChange={this.handleChange} />
                    </form>

                    <form>
                        <label>
                            Inscripcion:
                        </label>
                        <input
                            className="form-control"
                            name="inscripcion"
                            type="text"
                            onChange={this.handleChange} />
                    </form>
                </section>
<br/>
