import React, {useState} from 'react';
/*import "../Estilos/usuarios.css"*/
import "../Estilos/estilos2.css";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const Gestion_usuario = () => {
    const [fecha,setFecha] = useState ([]); //estados
    const [rol,setRol] = useState ([]);
    const [estado,setEstado] = useState ([]);
    

    const enviarAlBackend =() => {             //Funci칩n
        console.log( 'Fecha: ', fecha);        //imprime en consola
        console.log( 'Rol asignado: ', rol);
        console.log( 'Estado: ', estado);
        toast.success ('Cambios guardados');
        /*alert(rol);*/
    };

    return(
            <div className="login_developer">
                 <h3>Registro</h3>
                  <hr/>
                  <form> 
                  
                    
                    <div className="opciones">
                    <input className="seleccionar" name= "id" placeholder="Nombre completo"/>
                    <input className="seleccionar" name= "id" placeholder="Identificacion"/> 
                    
                    </div>
                    
                    <div className="opciones2">
                         
                    <input className="seleccionar" name= "id" placeholder="Correo"/>
                    <input className="seleccionar" name= "id" placeholder="Contrase침a"/>                      
                        <select required defaultValue={0} onChange={(e) => {setRol(e.target.value);}} >
                        <option disabled value={0}>
                            Rol de usuario
                        </option>
                        <option>Administrador</option>
                        <option>Lider</option>    
                        <option>Estudiante</option>               
                    </select>
                    </div>
                    <hr/>

                    
                   

                    <div className="botones">
                        <button type="button" onClick = {enviarAlBackend}>Guardar Datos</button> {/* ejecuta funcion enviaAlBakend al dar clic en bot칩n*/}
                        <button type ="reset"> Limpiar  </button>
                        <ToastContainer position="top-right" autoClose={4000}/>
                    </div>
            </form>
                
            </div>  
    )

};

export default Gestion_usuario
