import React from "react";
import "../Estilos/tablaadmin.css"
import { Link } from "react-router-dom";

class Tabla_proyectos extends React.Component{
    insertar= ()=>{
        var valorNuevo= {...this.state.form};
        valorNuevo.id=this.state.data.length+1;
        var lista= this.state.data;
        lista.push(valorNuevo);
        this.setState({ modalInsertar: false, data: lista });
      }
    
      handleChange = (e) => {
        this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };

      render() {
    return(
        

      <div className="con">
           <section className="login_Developer1">
          <h1>Registrar proyecto</h1>   
        <div className="campos1 ">
            <label >Nombre </label>
            <input type="text" className="form-control" />
            <label >Objetivos generales </label>
            <input type="text" className="form-control"  />
            <label >Objetivos especificos </label>
            <input type="text"  className="form-control"  />
            <label >Presupuesto </label>
            <input type="text" className="form-control"  />
            <label >Fecha inicial</label>
            <input type="date"  className="form-control"  />
        </div>

      
        <div className="campos2 ">   
            <label >Fecha final</label>
            <input type="date"  className="form-control"  />
            <label >Documento</label>
            <input type="text"  className="form-control"  />
            <label >Nombre del lider</label>
            <input type="text"  className="form-control"  />
            <label >Estado del proyecto</label>
            <select className="lista">
                <option classname="opt" disabled value={0}>
                    Estado del proyecto
                </option>
                    <option>Autorizado</option>
                    <option>No autorizado</option> 
                    <option>Pendiente</option>                
            </select>
            <label >Fase del proyecto</label>
            <select className="lista">
                <option classname="opt" disabled value={0}>
                   Fase del proyecto
                </option>
                    <option>Activo</option>
                    <option>Inactivo</option>               
            </select>
           
           
        </div>
        <div className="boton1">
           <button type="button" id="ingresar" >Insertar proyecto</button> 
          
           </div>
           <div className="boton">
        <button type="button" id="ingresar"><Link to="/src/pages/ListarProyect.jsx">Listar proyectos</Link></button> 
        </div>

        
      </section>    
</div>
    );
      }
}
export default Tabla_proyectos
