const PrivateRoute = ({ children }) => {
  const { isAuthenticated, isLoading, loginWithRedirect } = useAuth0();

  if (isLoading) return <ReactLoading type='cylon' color='#abc123' height={599} width={200} />; //verificar el reaclogin para css

  

  return <>{children}</>;
}

